function injectCSS() {
   if(document.getElementById("JEBE_SOCAIL_ONELINE_STYLE") == null){
    var cssText = ".side-item .side-item-body .fans{line-height:12px;*line-height:13px;}.side-item-body .fans .ad_friends_img_wrapper{float:left;*margin-top: -5px;}";
    var cssObj = document.createElement("style");
    cssObj.id = "JEBE_SOCAIL_ONELINE_STYLE";
    var IS_IE = navigator.userAgent.indexOf("MSIE") >= 0;
    if (IS_IE) {
        cssObj.type = "text/css";
        cssObj.styleSheet.cssText = cssText;
        document.getElementsByTagName("head")[0].appendChild(cssObj)
    } else {
        document.body.appendChild(cssObj);
        cssObj.innerHTML = cssText;
    }
   }
}

